import React, { useState } from 'react';
import Message from './Message';
import './ChatBox.css';

function ChatBox() {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([
    { role: 'bot', text: 'Xin chào! Hãy nhập vài từ để mình làm thơ cho bạn.' }
  ]);
  const [loading, setLoading] = useState(false);

  const handleSend = () => {
    if (!input.trim()) return;
    setMessages([...messages, { role: 'user', text: input }]);
    setLoading(true);
    // Mock trả về thơ sau 1s
    setTimeout(() => {
      setMessages(prev => ([
        ...prev,
        { role: 'bot', text: `Đây là bài thơ GPT-1 dựa trên: "${input}"\n\nTrời xanh mây trắng nhẹ bay,\n${input} dẫn lối thơ hay tuyệt vời.` }
      ]));
      setLoading(false);
    }, 1000);
    setInput('');
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  return (
    <div className="chatbox-container">
      <div className="messages-list">
        {messages.map((msg, idx) => (
          <Message key={idx} role={msg.role} text={msg.text} />
        ))}
        {loading && <Message role="bot" text="Đang sinh thơ..." />}
      </div>
      <div className="input-row">
        <input
          type="text"
          placeholder="Nhập vài từ..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          disabled={loading}
        />
        <button onClick={handleSend} disabled={loading || !input.trim()}>Gửi</button>
      </div>
    </div>
  );
}

export default ChatBox;
