import React from 'react';
import './Message.css';

function Message({ role, text }) {
  return (
    <div className={`message ${role}`}>
      {text.split('\n').map((line, index) => (
        <React.Fragment key={index}>
          {line}
          {index < text.split('\n').length - 1 && <br />}
        </React.Fragment>
      ))}
    </div>
  );
}

export default Message;
