import React from 'react';
import ChatBox from './components/ChatBox';
import './App.css';

function App() {
  const handleNewChat = () => {
    window.location.reload(); // Đơn giản: reload để làm mới chat
  };

  return (
    <div className="app-bg">
      <div className="topbar">
        <div className="topbar-left">
          <span className="logo-icon">
            {/* Modern AI/Poetry Icon */}
            <svg width="28" height="28" viewBox="0 0 28 28" fill="none">
              <g>
                <circle cx="14" cy="14" r="13" fill="#fff" opacity="0.2"/>
                <circle cx="14" cy="14" r="10" fill="#fff" opacity="0.4"/>
                <path d="M14 8c-1.5 0-2.7 1.2-2.7 2.7v0.6c0 0.3-0.2 0.5-0.5 0.5h-0.6c-1.5 0-2.7 1.2-2.7 2.7s1.2 2.7 2.7 2.7h0.6c0.3 0 0.5 0.2 0.5 0.5v0.6c0 1.5 1.2 2.7 2.7 2.7s2.7-1.2 2.7-2.7v-0.6c0-0.3 0.2-0.5 0.5-0.5h0.6c1.5 0 2.7-1.2 2.7-2.7s-1.2-2.7-2.7-2.7h-0.6c-0.3 0-0.5-0.2-0.5-0.5v-0.6C16.7 9.2 15.5 8 14 8z" fill="#fff"/>
                <circle cx="14" cy="14" r="2.5" fill="#fff" opacity="0.8"/>
                <path d="M12 10l4 8M16 10l-4 8" stroke="#fff" strokeWidth="0.8" opacity="0.6"/>
              </g>
            </svg>
          </span>
          <span className="topbar-title">Thơ AI</span>
        </div>
        <button className="topbar-newchat" onClick={handleNewChat}>
          <svg width="16" height="16" viewBox="0 0 16 16" fill="none" style={{marginRight: '8px'}}>
            <path d="M8 1v14M1 8h14" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
          </svg>
          Tạo chat mới
        </button>
        <div className="topbar-right"></div>
      </div>
      <div className="chat-wrapper">
        <ChatBox />
      </div>
    </div>
  );
}

export default App;
